query:
{
  lessonCollection(where: {OR: [{title_contains: "content"}, {title_contains: "SDK"}]}) {
    items {
      title
      slug
      modulesCollection(limit: 2, skip: 1) {
        total
        limit
        skip
        items {
          ...imageUrl
          ... on LessonCodeSnippets {
            title
          }
          ... on LessonCopy {
            sys {
              id
            }
            title
          }
        }
      }
    }
  }
}

fragment imageUrl on LessonImage {
  title
  image {
    url
  }
}
